import { useState } from "react";
import axios from "axios";
import { useLocation, useNavigate } from "react-router-dom";

function VerifyOtp() {
  const navigate = useNavigate();
  const location = useLocation();

  // Get email from signup page
  const email = location.state?.email;

  const [otp, setOtp] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  // Safety check
  if (!email) {
    return <h3>Email not found. Please signup again.</h3>;
  }

  const handleSubmit = async (e) => {
    e.preventDefault(); // üî¥ VERY IMPORTANT
    setLoading(true);
    setMessage("");

    try {
      const res = await axios.post(
        "http://localhost:5000/api/auth/OT",
        { email, otp }
      );

      setMessage(res.data.message || "OTP Verified ‚úÖ");

      // ‚úÖ Go to login after success
      setTimeout(() => {
        navigate("/login");
      }, 1500);

    } catch (error) {
      setMessage(
        error.response?.data?.message || "Invalid OTP ‚ùå"
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container d-flex justify-content-center align-items-center min-vh-100">
      <div className="card p-4 shadow" style={{ maxWidth: "400px", width: "100%" }}>
        <h2 className="text-center mb-3">Verify OTP</h2>

        {message && (
          <div className="alert alert-info text-center">
            {message}
          </div>
        )}

        {/* ‚úÖ FORM IS REQUIRED */}
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label">Enter OTP</label>
            <input
              type="text"
              className="form-control"
              placeholder="6-digit OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              required
            />
          </div>

          {/* ‚úÖ BUTTON MUST BE SUBMIT */}
          <button
            type="submit"
            className="btn btn-primary w-100"
            disabled={loading}
          >
            {loading ? "Verifying..." : "Verify OTP"}
          </button>
        </form>
      </div>
    </div>
  );
}

export default VerifyOtp;
