import { useState } from 'react';
import './App.css';
function Login(){
   const [formData, setFormData]= useState({
    email:"",
    password:""
   });
   const [message, setMessage]=useState("");
   const [loading, setLoading]=useState(false);
   const handleChange = (e)=> {
    setFormData({...formData, [e.target.name]:e.target.values});
   };
     const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      const res = await axios.post(
        "http://localhost:5000/api/auth/login",
        formData
      );

      setMessage(res.data.message || "login successful üéâ");

      // ‚úÖ Redirect to login after 1.5 seconds
      setTimeout(() => {
        navigate("/Home");
      }, 1500);

    } catch (err) {
      setMessage(
        err.response?.data?.message || "login failed ‚ùå"
      );
    } finally {
      setLoading(false);
    }
  };
    return(

        <div>
        <div className=' container mt-5 col-md-4 '>
            <div className= "card p-4" style={{ maxWidth: "400px", width: "100%" }}>
            <h1 className='text-center text-white'>login</h1>
            <form onSubmit={handleSubmit}>
                
          {/* Password */}
          <div className="mb-3">
            <label className="form-label text-white">email</label>
            <input
              type="email"
              name='email'
              onChange={handleChange}
              className="form-control"
              placeholder="Email"
              required
            />
          </div>

          {/* Confirm Password */}
          <div className="mb-auto">
            <label className="form-label text-white">  Password</label>
            <input
              type="password"
              name='password'
              className="form-control"
              onChange={handleChange}
              placeholder="   Password"
              required
            />
            <a className='  text-primery' href='foget password '>foget password </a>
             </div>
          <button
            type="submit"
            className="btn btn-send w-100"
            disabled={loading}
          >
            {loading ? "login..." : "Login"}
          </button>        
            </form>
        </div>
        </div>
        </div>
    );
}
export default Login;
